// Split the string to an array to iterate over.
$instructions = str_split($input);

// Santa starts at floor 0.
$floor = 0;

// He hasn't been in the basement yet.
$basement = 0;

// Get the key and value of every instruction.
foreach ($instructions as $key => $direction) {
    // Check the direction.
    if ($direction == ")") {
        // Go down a floor.
        $floor--;

        // Is this the first time Santa is in the basement?
        if ($floor == -1 && $basement == 0) {
            $basement = $key + 1;
        }
    } else {
        // Go up a floor.
        $floor++;
    }
}

// The answer.
echo "Santa ends on floor nr. " . $floor . " and he first entered the basement at level " . $basement . "!";
